package javaloops;

public class Sample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int sum = 0;
        int natural = 1000;
        
        for(int i = 0; i <= natural; i ++) {
        	sum = sum + i;
        }
        
        System.out.println("The sum of the natural numbers is : " +sum);
	}

}
